else if (msg.payload === "pending") { msg.payload = '<img class="blink-image" src="' + imagepath + 'pending.png">'; }
else if (msg.payload === "disarmed") { msg.payload = '<img class="marginauto" src="' + imagepath + 'unarmed.png">'; }
else if (msg.payload === "warning") { msg.payload = '<img class="marginauto" src="' + imagepath + 'warning.png">'; }
